mapper.select:
from members
where id like #{bindValue} or email like #{bindValue} or name like #{bindValue} or cell_phone like #{bindValue}
order by member_id desc

select id
from members
where email=#{email} and question=#{question} and answer=#{answer}

select id, email
from members
where id=#{id} and email=#{email} and question=#{question} and answer=#{answer}

mapper.update:
update members
certification_flag=1, authkey='COMPLETE'
where id=#{id} and authkey=#{authkey}

 update members set
     nickname=#{nickname}
where id=#{id} and #{nickname} is not null and #{nickname} != ''

mapper.select:
select count(*)
from members
where id=#{userId} and password=#{userPw}
