mapper.select:
select p.product_id, p.group_id, p.member_id, p.price, m.name, p.product_name, p.registered_date, p.quantity, p.description, g.group_name, g.category_id, c.category_name, f.product_id, f.photo_path from products p left outer join product_photos f on (p.product_id = f.product_id AND f.main_photo_flag = 0) left outer join groups g on g.group_id = p.group_id left outer join categorys c on c.category_id = g.category_id left outer join sellers s on s.member_id = p.member_id left outer join members m on m.member_id = s.member_id
where g.category_id=#{groupNo} and p.member_id = ${memberNo}
order by p.product_id DESC

mapper.update:
update products set
quantity=#{quantity},
description=#{description}
where product_id=#{no}

update products set
recommend_count = recommend_count +1
where product_id = #{value}

update products set
recommend_count = recommend_count -1
where product_id = #{value}

mapper.select:
select p.product_id, p.group_id, p.member_id, p.price, p.product_name, p.registered_date, p.quantity, p.description, g.group_name, g.category_id, c.category_name, f.product_id, f.photo_path from products p left outer join product_photos f on (p.product_id = f.product_id AND f.main_photo_flag = 0) left outer join groups g on g.group_id = p.group_id left outer join categorys c on c.category_id = g.category_id
where g.category_id=#{no}
order by p.product_id DESC
